  //   // kenalkan stokproduk dengan stokproduk yang baru
  //   this.setState({ stokProduk })
  // }

  // masukAdmin(e) {
  //   e.preventDefault()
  //   if (e.target.user.value === 'fazufi' && e.target.pass.value === 'fazufi') {
  //     localStorage.setItem('token', '1klsdfjsalkjfjfkdklsdjfsfjejr')
  //     this.props.history.push('/admin')
  //   } else {
  //     alert('incorrect password')
  //   }
  // }

  // onAdd = (e) => {
  //   e.preventDefault();
  //   const stokProduk = this.state.stokProduk;
  //   stokProduk.push({
  //     nama: e.target.nama.value,
  //     harga: parseInt(e.target.harga.value),
  //   });
  //   localStorage.setItem("stokProduk", JSON.stringify(stokProduk));
  //   // kenalkan stokproduk dengan stokproduk yang baru
  //   this.setState({ stokProduk });
  // };
  onSave = (e) => {
    e.preventDefault();
    const UserName=e.target.Username.value, Name= e.target.Name.value, Bio= e.target.Bio.value,  Website= e.target.Website.value,
    Email= e.target.Email.value,
    PhoneNumber= e.target.PhoneNumber.value,
    Gender= e.target.Gender.value
    console.log(e.target.Username.value);
    // const ProfileSaved = this.state.ProfileSaved;
    // this.setState({ ProfileSaved: ProfileSaved });
    // ProfileSaved.push({ nama: e.target.UserName.value })
    localStorage.setItem("ProfileSaved", JSON.stringify({Name: Name,
      UserName: UserName,
      Website: Website,
      Bio: Bio,
      Email: Email,
      PhoneNumber: PhoneNumber,
      Gender: Gender})

      );
  };

  render() {
    // console.log(this.onSave);
    return (
      // <div>
      <Template {...this.props}>
        {/* <Form onSubmit={(e)=>this.onSave}> */}
        {/* <button onClick={}> gfjyhtgfuyf</button> */}

        {/* <Form onSubmit={this.onSave}> */}
        <Form onSubmit={this.onSave}>
        <Container>
          <Row>
            <Col md={2}>
              <img
                className="float-right"
                src="https://instagram.fada2-1.fna.fbcdn.net/v/t51.2885-19/44884218_345707102882519_2446069589734326272_n.jpg?_nc_ht=instagram.fada2-1.fna.fbcdn.net&_nc_ohc=SkQvnEdYBN8AX-tYmCd&oh=c42861bdaa4bb6a18094f8bf99bb7f78&oe=6057FD8F&ig_cache_key=YW5vbnltb3VzX3Byb2ZpbGVfcGlj.2"
                alt="pp akun"
                width="40"
                height="40"
              />
            </Col>
            <Col md={10}>
              <h5 className="mb-0">UserName Account</h5>
              <p className="mt-0">
                <b>
                  {/* <a href="/explore/locations/"><div class="_7UhW9  PIoXz       MMzan   _0PwGv         uL8Hv         ">Locations</div></a> */}
                  <a href="C:/home">Change Profile Photo</a>
                </b>
              </p>
            </Col>
          </Row>
          <Row>
            <Col md={2}>
              <p className="float-right">
                <strong>Name</strong>
              </p>
            </Col>
            <Col md={10}>
              <Form.Group>
                <Form.Control
                  name="Name"
                  type="text"
                  placeholder="type your name"
                />
                <Form.Text className="text-muted">
                  Help people discover your account by using the name you're
                  known by: either your full name, nickname, or business name.
                  <br />
                  <br />
                  You can only change your name twice within 14 days.
                </Form.Text>
              </Form.Group>
            </Col>
          </Row>
          <Row>
            <Col md={2}>
              <p className="float-right">
                <strong>Username</strong>
              </p>
            </Col>
            <Col md={10}>



                <Form.Group>
                  <Form.Control type="text"
                    name="Username"                   
                    placeholder="type your UserName account"
                  />
                  <Form.Text className="text-muted">
                    In most cases, you'll be able to change your username back
                    to alifaholshop3066 for another 14 days.
                    <a href="https://help.instagram.com/876876079327341">
                      Learn More
                    </a>
                  </Form.Text>
                </Form.Group>
                <Button type="submit">Submit</Button>





            </Col>
          </Row>
